import "./sideBar.scss";
import React, { useContext, useState } from "react";
import { Context } from "../../hooks/store";
import { Link } from "react-router-dom";
import WbCloudyRoundedIcon from "@material-ui/icons/WbCloudyRounded";
import DashboardRoundedIcon from "@material-ui/icons/DashboardRounded";
import IconButton from "@material-ui/core/IconButton";
import AccountBoxRoundedIcon from "@material-ui/icons/AccountBoxRounded";
import { makeStyles } from "@material-ui/core/styles";
import { useMediaQuery } from "react-responsive";
import Tooltip from "../../components/tooltip/tooltip";
import Logo from "../../assets/images/logo.png";
import usersIcon from "../../assets/images/usersIcon.svg";
import usersIconWhite from "../../assets/images/usersIconWhite.svg";
import useCaseIcon from "../../assets/images/useCaseIcon.svg";
import useCaseIconWhite from "../../assets/images/useCaseIconWhite.svg";
import visualizationIcon from "../../assets/images/visualizationIcon.svg";
import visualizationIconWhite from "../../assets/images/visualizationIconWhite.svg";
import pathControllers from "../../router";
import config from "../../config/config.json"
import { ApiEndpoint } from "../../apiEndpoints.model";
import { httpRequest } from "../../services/http";


const useStyles = makeStyles((theme) => ({
  iconSize: {
    "& svg": {
      fontSize: 27,
    },
  },
  blink: {
    "& svg": {
      fontSize: 27,
    },
    backgroundColor: "rgba(0,0,0,0.04)",
    animation: "blink 1s",
    animationIterationCount: "infinite"
  }
}));

const Desktop = ({ children }) => {
  const isDesktop = useMediaQuery({ minWidth: 850 });
  return isDesktop ? children : null
}

function SideBar() {
  const classes = useStyles();
  const [state, dispatch] = useContext(Context);
  const [disabledBlink, setdisabledBlink] = useState(state.userData?.already_create_usecase_flag || false)


  const setCreateUseCase = async () => {
    if (!disabledBlink || !state.userData?.already_create_usecase_flag) {
      try {
        const data = await httpRequest("POST", ApiEndpoint.CREATE_USECASE_FLAG);
        if (data && data !== undefined) {
          dispatch({ type: "SET_USER_DATA", payload: data });
          setdisabledBlink(true)
        }
      } catch (err) { }
    }
  };

  return (
    <Desktop>
      <div className="sidebar">
        {state.showPanel &&
          <div>
            <Link to={pathControllers.overview}>
              <img src={Logo} className="sidebar-logoimg" alt="logo" />
            </Link>
            <div className="sidebar-icons">
              <Link to={pathControllers.overview}>
                <IconButton className={classes.iconSize}>
                  <Tooltip text="Overview">
                    <div
                      className={
                        state.route === "overview"
                          ? "sidebar-circle-icon sidebar-icon-checked "
                          : "sidebar-circle-icon sidebar-icon"
                      }
                    >
                      <DashboardRoundedIcon />
                    </div>
                  </Tooltip>
                </IconButton>
              </Link>

              <Link
                to={pathControllers.usecases}
                onClick={() => {
                  dispatch({ type: "IS_USECASE_BUILDER_ACTIVE", payload: false });
                  dispatch({ type: "IS_USECASE_EDIT_ACTIVE", payload: false });
                  setCreateUseCase();
                }}
              >
                <Tooltip text="Use cases">
                  <IconButton className={disabledBlink || state.userData?.already_create_usecase_flag ? classes.iconSize : classes.blink}>
                    <div
                      className={
                        state.route === "usecases"
                          ? "sidebar-circle-icon sidebar-icon-checked "
                          : "sidebar-circle-icon sidebar-icon"
                      }
                    >
                      {state.route === "usecases" ? (
                        <img
                          src={useCaseIconWhite}
                          alt="useCaseIconWhite"
                          width="27"
                          height="27"
                        ></img>
                      ) : (
                        <img
                          src={useCaseIcon}
                          alt="useCaseIcon"
                          width="27"
                          height="27"
                        ></img>
                      )}

                    </div>
                  </IconButton>
                </Tooltip>
              </Link>


              <Link to={pathControllers.users}>
                <Tooltip text="Users">
                  <IconButton className={classes.iconSize}>
                    <div
                      className={
                        state.route === "users"
                          ? "sidebar-circle-icon sidebar-icon-checked "
                          : "sidebar-circle-icon sidebar-icon"
                      }
                    >
                      {state.route === "users" ? (
                        <img
                          src={usersIconWhite}
                          alt="usersIconWhite"
                          width="27"
                          height="27"
                        ></img>
                      ) : (
                        <img
                          src={usersIcon}
                          alt="usersIcon"
                          width="27"
                          height="27"
                        ></img>
                      )}
                    </div>
                  </IconButton>
                </Tooltip>
              </Link>
              <Link to={pathControllers.visualizations}>
                <Tooltip text="Visualizations" white={false}>
                  <IconButton className={classes.iconSize}>
                    <div
                      className={
                        state.route === "visualizations"
                          ? "sidebar-circle-icon sidebar-icon-checked "
                          : "sidebar-circle-icon sidebar-icon"
                      }
                    >
                      {state.route === "visualizations" ? (
                        <img
                          src={visualizationIconWhite}
                          alt="visualizationIconWhite"
                          width="27"
                          height="27"
                        ></img>
                      ) : (
                        <img
                          src={visualizationIcon}
                          alt="visualizationIcon"
                          width="27"
                          height="27"
                        ></img>
                      )}
                    </div>
                  </IconButton>
                </Tooltip>
              </Link>

              <Link to={pathControllers.marketplace}>
                <Tooltip text="Marketplace">
                  <IconButton className={classes.iconSize}>
                    <div
                      className={
                        state.route === "marketplace"
                          ? "sidebar-circle-icon sidebar-icon-checked "
                          : "sidebar-circle-icon sidebar-icon"
                      }
                    >
                      <WbCloudyRoundedIcon />
                    </div>
                  </IconButton>
                </Tooltip>
              </Link>
            </div>
            <div className="sidebar-supprt">
              <Link to={pathControllers.account}>
                <Tooltip text="Account">
                  <IconButton className={classes.iconSize}>
                    <div
                      className={
                        state.route === "account"
                          ? "sidebar-circle-icon sidebar-icon-checked "
                          : "sidebar-circle-icon sidebar-icon"
                      }
                    >
                      <AccountBoxRoundedIcon />
                    </div>
                  </IconButton>
                </Tooltip>
              </Link>
              <a href="https://docs.strech.io" target="_blank" rel="noreferrer">
                <Tooltip text="Support">
                  <IconButton className={classes.iconSize}>
                    <div className="sidebar-circle-icon sidebar-icon-support ">
                      <img
                        alt="help"
                        height="25px"
                        width="25px"
                        src="data:image/svg+xml;base64,"
                      />
                    </div>
                  </IconButton>
                </Tooltip>
              </a>
            </div>
          </div>}
      </div>
    </Desktop>

  );
}

export default SideBar;
